import { HYDRATE } from 'next-redux-wrapper';

const initialState = {
    user: {
        
    },
    post: {

    }
};

export const loginAction = (data) => {
    return {
        type: 'LOG_IN',
        data,
    }
}
 
export const logoutAction = (data) => {
    return {
        type: 'LOG_OUT',
    }
}

// (이전상태, 액션) => 다음상태
const rootReducer = (state = initialState, action) => {
    switch (action.type) {
        case HYDRATE:
            console.log('HYDRATE');
            return { ...state, ...action.payload };
        case 'LOG_IN':
            return {
                ...state,
                user: {
                    ...state.user,
                    isLoggedIn: true,
                    name: action.data,
                },
            };
        case 'LOG_OUT':
            return {
                ...state,
                user: {
                    ...state.user,
                    isLoggedIn: false,
                    name: null,
                },
            };
        default:
            return state;
    }
};

export default rootReducer;
